import React from 'react';

export default function ProductList({ products }) {
  return (
    <table className="w-full text-left border">
      <thead>
        <tr>
          <th>Store</th><th>Name</th><th>Price</th><th>Discount (%)</th><th>Value/Unit</th>
        </tr>
      </thead>
      <tbody>
        {products.map((p, i) => (
          <tr key={i}>
            <td>{p.store}</td>
            <td>{p.name}</td>
            <td>{p.price.toFixed(2)}</td>
            <td>{p.discount}</td>
            <td>{(p.price / p.grammage).toFixed(2)}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
