import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Filters from './components/Filters';
import ProductList from './components/ProductList';
import PriceHistoryChart from './components/PriceHistoryChart';

export default function App() {
  const [products, setProducts] = useState([]);
  const [store, setStore] = useState('');
  const [selectedName, setSelectedName] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  useEffect(() => {
    const params = new URLSearchParams();
    if (store) params.append('store', store);
    if (dateFilter) params.append('date', dateFilter);

    axios.get(`/api/products?${params.toString()}`)
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, [store, dateFilter]);

  // derive unique product names for dropdown
  const productNames = Array.from(new Set(products.map(p => p.name))).sort();

  // apply dropdown filter
  const filtered = products.filter(p =>
    (!selectedName || p.name === selectedName)
  );

  return (
    <div className="p-4">
      <h1 className="text-xl mb-4">Price Comparator</h1>

      <Filters
        store={store}
        setStore={setStore}
        selectedName={selectedName}
        setSelectedName={setSelectedName}
        dateFilter={dateFilter}
        setDateFilter={setDateFilter}
        productNames={productNames}
      />

      <ProductList products={filtered} />

      {filtered.length > 0 && (
        <PriceHistoryChart product={filtered[0]} />
      )}
    </div>
  );
}
