import React, { useState } from 'react';
import axios from 'axios';

export default function BasketBuilder({ onResult }) {
  const [lines, setLines] = useState([{ name: '', qty: '' }]);

  const addLine = () =>
    setLines(prev => [...prev, { name: '', qty: '' }]);

  const updateLine = (index, key, value) => {
    const updated = [...lines];
    updated[index][key] = value;
    setLines(updated);
  };

  const optimize = () => {
    axios
      .post('/api/basket/optimize', {
        items: lines.map(line => ({
          productName: line.name,
          quantity: parseFloat(line.qty) || 0,
        })),
      })
      .then(response => onResult(response.data))
      .catch(err => console.error(err));
  };

  return (
    <div className="mb-4">
      {lines.map((line, i) => (
        <div key={i} className="flex space-x-2 mb-2">
          <input
            type="text"
            className="border p-1 flex-1"
            placeholder="Product Name"
            value={line.name}
            onChange={e => updateLine(i, 'name', e.target.value)}
          />
          <input
            type="number"
            className="border p-1 w-20"
            placeholder="Qty"
            value={line.qty}
            onChange={e => updateLine(i, 'qty', e.target.value)}
          />
        </div>
      ))}
      <button
        onClick={addLine}
        className="mr-2 px-3 py-1 bg-blue-600 text-white rounded"
      >
        + Line
      </button>
      <button
        onClick={optimize}
        className="px-3 py-1 bg-green-600 text-white rounded"
      >
        Optimize Basket
      </button>
    </div>
  );
}
