import React from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

export default function PriceHistoryChart({ product }) {
  const data = [
    { date: '2025-05-01', price: product.price * 1.00 },
    { date: '2025-05-05', price: product.price * 0.95 },
    { date: '2025-05-08', price: product.price * 1.02 }
  ];

  return (
    <div className="mt-6" style={{ width: '100%', height: 300 }}>
      <ResponsiveContainer>
        <LineChart data={data}>
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Line dataKey="price" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}
